SouthRidge.Utils.Uploader.ImageFile=function(a){SouthRidge.Utils.CheckConnection()&&forge.request.ajax({url:"https://api.parse.com/1/files/"+(new Date).getTime()+".jpg",headers:{"X-Parse-Application-Id":SouthRidge.Utils.ParseAppId,"X-Parse-REST-API-Key":SouthRidge.Utils.ParseRestKey},type:"POST",files:[a],fileUploadMethod:"raw",dataType:"json",success:function(a){SouthRidge.Utils.Uploader.ImageMetaData(a)},error:function(){SouthRidge.Utils.Alert("Problem uploading photo to South Ridge.  Or location permission required.")}})};
SouthRidge.Utils.Uploader.ImageMetaData=function(a){forge.request.ajax({url:"https://api.parse.com/1/classes/Photo",headers:{"X-Parse-Application-Id":SouthRidge.Utils.ParseAppId,"X-Parse-REST-API-Key":SouthRidge.Utils.ParseRestKey},type:"POST",contentType:"application/json",dataType:"json",data:JSON.stringify({file:{__type:"File",name:a.name},stream:"south-ridge-upload"}),success:function(){SouthRidge.Utils.Alert("Photo successfully uploaded to South Ridge for review.")},error:function(){SouthRidge.Utils.Alert("Problem uploading photo metadata to South Ridge.")}})};
